import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../index.css'
import '../styles/personalInfo.scss'
import buttonIcon from '../images/buttonIcon.png'

const PersonalInformation = () => {
    const navigate = useNavigate();
    const [massage, setMassage] = useState('')
    let className = ''
    const handleChange = event => {
        setMassage(event.target.value);
      };
    const handleClick = event => {
        event.preventDefault();
        var abff = document.querySelectorAll('input')
        abff.map(inputs => {
            if(massage.trim().length <=2){
                inputs.classList.add('inputError');
                console.log(className)
            }else{
                console.log(event)
                abff.classList.remove('inputError');
            }
        })
    }
    return (
        <section className='personalInfo'>
            <div className="rightHeader">
                <span>Start Creating Your Account</span>
            </div>
            <div className="steps">
                <ul className="progressbar">
                <div className="line"></div>
                    <li><span>Personal information</span></li>
                    <li><span>Chess experience</span></li>
                </ul>
		    </div>
            <div className="textDiv">
                <span className='bigTxt'>Personal information</span>
                <div>
                    <span className='smallTxt'>This is Basic Informaton Fields</span>
                </div>
            </div>
            <form action="">
                <div className="formInputs">
                    <input 
                        id='name'
                        type="text"
                        name="message" 
                        className={className}
                        onChange={handleChange} 
                        autoComplete="off" 
                        placeholder='Name*' required
                    />
                </div>
                <div className="formInputs">
                    <input type="email" placeholder='Email address*'required/>
                </div>
                <div className="formInputs">
                    <input type="tel" placeholder='Phone number*' required/>
                </div>
                <div className="formInputs">
                    <input type="text" placeholder='Date of birth*' required/>
                </div>
            </form>
            <div className="buttons">
                <button className='backBtn' onClick={() => navigate(-1)}>Back</button>
                <button className='nextBtn' onClick={handleClick}>Next<img src={buttonIcon} alt="" /></button>
            </div>
        </section>
    )
}

export default PersonalInformation
